mapper.select:
with call_stats as (select v.TASK_ID AS TASK_ID, count(distinct v.CALL_ID) AS CALL_CNT, count(distinct c.counselor_cd) AS COUNSELOR_CNT, count(distinct c.cust_num) AS CUST_CNT from v_mstr_call_task v join mstr_call c on v.CALL_ID = c.call_id
AND v.TASK_ID IN
#{item}
AND C.CALL_DT >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND C.CALL_DT <= STR_TO_DATE(#{endDate},'%Y-%m-%d')
AND C.COUNSELOR_CD = #{counselor}
AND v.TASK_ID = #{product}
AND c.CALL_ID IN ( SELECT CALL_ID FROM MSTR_ANALYSIS_RESULT WHERE ITEM_ID = 'I0034' AND CONTENT_ID = 'C0097' AND RESULT_VALUE = #{csAgmt} )
AND c.CALL_ID IN ( SELECT CALL_ID FROM MSTR_ANALYSIS_RESULT WHERE ITEM_ID = 'I0043' AND CONTENT_ID = 'C0117' AND RESULT_VALUE = #{ncSupport} )
group by v.TASK_ID) select call_stats.TASK_ID AS taskId , '콜수' AS name , call_stats.CALL_CNT AS cnt , 100.0 AS ratio from call_stats union all select call_stats.TASK_ID AS taskId, '상담사수' AS name, call_stats.COUNSELOR_CNT AS cnt, round(((call_stats.COUNSELOR_CNT * 100.0) / nullif(call_stats.CALL_CNT, 0)), 1) AS ratio from call_stats union all select call_stats.TASK_ID AS taskId, '고객수' AS name, call_stats.CUST_CNT AS cnt, round(((call_stats.CUST_CNT * 100.0) / nullif(call_stats.CALL_CNT, 0)), 1) AS ratio from call_stats order by taskId, name

with call_stats as ( SELECT MAR.task_id AS TASK_ID, MAR.type_id AS TYPE_ID, CASE WHEN GROUPING(MAR.item_id) = 1 THEN 'I0000' ELSE MAR.item_id END AS ITEM_ID, COUNT(DISTINCT MAR.call_id) AS CNT FROM mstr_analysis_result MAR LEFT JOIN MSTR_CALL MC ON MAR.call_id = MC.call_id
AND MAR.result_value = 'Y'
AND MC.CALL_DT >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND MC.CALL_DT <= STR_TO_DATE(#{endDate},'%Y-%m-%d')
AND MC.COUNSELOR_CD = #{counselor}
AND MC.CALL_ID IN ( SELECT CALL_ID FROM MSTR_ANALYSIS_RESULT WHERE ITEM_ID = 'I0034' AND CONTENT_ID = 'C0097' AND RESULT_VALUE = #{csAgmt} )
AND MC.CALL_ID IN ( SELECT CALL_ID FROM MSTR_ANALYSIS_RESULT WHERE ITEM_ID = 'I0043' AND CONTENT_ID = 'C0117' AND RESULT_VALUE = #{ncSupport} )
GROUP BY MAR.task_id, MAR.type_id, MAR.item_id WITH ROLLUP HAVING NOT (MAR.task_id IS NULL OR MAR.type_id IS NULL) ORDER BY TASK_ID, TYPE_ID, ITEM_ID ) select a.TASK_ID AS "taskId", a.TYPE_ID AS "typeId", a.ITEM_ID AS "itemId", if((a.ITEM_ID = 'I0000'), '전체', b.name) AS "name", a.CNT AS "cnt", round(((a.CNT * 100) / ( select COUNT(*) from V_MSTR_CALL_TASK VMCT LEFT JOIN MSTR_CALL MCS ON VMCT.CALL_ID = MCS.CALL_ID
AND VMCT.TASK_ID = a.TASK_ID
AND MCS.CALL_DT >= STR_TO_DATE(#{startDate},'%Y-%m-%d')
AND MCS.CALL_DT <= STR_TO_DATE(#{endDate},'%Y-%m-%d')
AND MCS.COUNSELOR_CD = #{counselor}
)),1) AS "ratio" from (call_stats a left join mstr_eval_item b on a.TASK_ID = b.task_id and a.TYPE_ID = b.type_id and a.ITEM_ID = b.item_id )
AND a.TASK_ID IN
#{item}
AND a.TYPE_ID IN
#{item}
AND a.TASK_ID = #{product}
